package main

import (
	"fmt"
	"time"
)

func main() {
	start := time.Now()
	fmt.Println(detectCapitalUse(""))
	finish := time.Since(start)
	fmt.Printf("Ex time - %v\n", finish)
}

func detectCapitalUse(word string) bool {
	return checkAllLowerCase(word) || checkAllUpperCase(word) || checkCapitalLetter(word)
}

func checkAllUpperCase(word string) bool {
	for _, letter := range word {
		if letter < 65 || letter > 90 {
			return false
		}
	}
	return true
}

func checkAllLowerCase(word string) bool {
	for _, letter := range word {
		if letter < 97 || letter > 122 {
			return false
		}
	}
	return true
}

func checkCapitalLetter(word string) bool {
	if word[0] > 65 || word[0] < 90 {
		if ok := checkAllLowerCase(word[1:]); !ok {
			return false
		}
	}
	return true
}
